// src/components/QuizComponent.vue
<template>
  <div class="quiz-container">
    <header>
      <div class="header-content">
        <h1>Спортмастер</h1>
        <div v-if="currentUser" class="user-info">
          {{ currentUser.username }}
        </div>
      </div>
    </header>

    <div class="content">
      <div v-if="showResult" class="quiz-result">
        <h2>Результаты викторины</h2>
        <p>Вы набрали {{ score }} баллов из {{ questions.length }}.</p>
        <button @click="restartQuiz" class="restart-button">Начать заново</button>
      </div>

      <div v-else-if="currentQuestion" class="quiz-question">
        <h2>{{ currentQuestion.question_text }}</h2>
        <ul>
          <li v-for="answer in currentQuestion.answers" :key="answer.answer_id">
            <button @click="checkAnswer(answer)" class="answer-button">{{ answer.answer_text }}</button>
          </li>
        </ul>
      </div>

      <div v-else class="loading">
        Loading...
      </div>
    </div>
  </div>
</template>

<script>
import axios from 'axios';
import { mapGetters } from 'vuex';

export default {
  data() {
    return {
      questions: [],
      currentQuestionIndex: 0,
      score: 0,
      showResult: false
    };
  },
  computed: {
    ...mapGetters(['currentUser']),
    currentQuestion() {
      return this.questions[this.currentQuestionIndex];
    }
  },
  methods: {
    async fetchQuestions() {
      try {
        const response = await axios.get('http://localhost:3000/api/questions');
        this.questions = response.data;
      } catch (error) {
        console.error('Error fetching questions:', error);
      }
    },
    checkAnswer(answer) {
      if (answer.is_correct) {
        this.score++;
      }
      if (this.currentQuestionIndex + 1 < this.questions.length) {
        this.currentQuestionIndex++;
      } else {
        this.showResult = true;
      }
    },
    restartQuiz() {
      this.currentQuestionIndex = 0;
      this.score = 0;
      this.showResult = false;
    }
  },
  mounted() {
    this.fetchQuestions();
  }
};
</script>

