.quiz-container {
  background-color: #f7f7f7;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

header {
  background-color: #1E2ED4;
  color: #FFF;
  padding: vue
20px;
  text-align: center;
  border-radius: 10px 10px 0 0;
}

.header-content {
  max-width: 100%;
  margin: 0 auto;
  padding: 0 20px; /* Добавлено для центрирования контента в шапке */
}

h1 {
  font-size: 1.8em;
  margin: 0;
}

.content {
  max-width: 600px;
  margin: 20px auto; /* Центрирование основного контента */
  padding: 20px;
}

.quiz-question,
.quiz-result,
.loading {
  margin-top: 20px;
  padding: 20px;
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}

.quiz-question h2,
.quiz-result h2 {
  margin-bottom: 20px;
  font-size: 1.5em;
}

ul {
  list-style-type: none;
