  font-size: 1.2em;
}

.restart-button {
  padding: 10px 20px;
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
}

.restart-button:hover {
  background-color: #218838;
}

.user-info {
  font-size: 1em;
  color: white;
}
</style>
