// src/main.js
import { createApp } from 'vue';
import App from './App.vue';
import axiosInstance from './api/axios'; // Импортируем настроенный экземпляр Axios
import router from './router';

const app = createApp(App);
app.config.globalProperties.$axios = axiosInstance; // Делаем его доступным как глобальное свойство
app.use(router);
app.mount('#app');
