import { createRouter, createWebHistory } from 'vue-router';
import LoginComponent from '../components/LoginComponent.vue'; // Замените на реальное название компонента

const routes = [
  { path: '/', redirect: '/login' }, // Перенаправление с корневого пути на /login
  { path: '/login', component: LoginComponent },
  { path: '/register', component: RegisterComponent },
  { path: '/quiz', component: QuizComponent, meta: { requiresAuth: true } },
  // другие маршруты
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
